import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = 'https://lnbczvfntvqximviyrxm.supabase.co/';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImxuYmN6dmZudHZxeGltdml5cnhtIiwicm9sZSI6ImFub24iLCJpYXQiOjE2ODczNzgzMjcsImV4cCI6MjAwMjk1NDMyN30.sJTtkRZCutBVfCG7kH4ESQAH5F2f8kFQXQYMtXLfUrA';
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  async function fetchTodos() {
    const { data, error } = await supabase.from('todos').select('*');
    if (error) {
      console.log('Error fetching todos:', error.message);
      return;
    }
    setTodos(data);
  }

  async function addTodo() {
    if (!newTodo) return;
    const { data, error } = await supabase.from('todos').insert([{ task: newTodo }]);
    if (error) {
      console.log('Error adding todo:', error.message);
      return;
    }
    setTodos([...todos, ...data]);
    setNewTodo('');
  }

  async function deleteTodo(id) {
    const { error } = await supabase.from('todos').delete().match({ id });
    if (error) {
      console.log('Error deleting todo:', error.message);
      return;
    }
    setTodos(todos.filter((todo) => todo.id !== id));
  }

  async function createCalendarEvent() {
    const { user, error: authError } = await supabase.auth.signIn({
      provider: 'google',
    });

    if (authError) {
      console.log('Error signing in:', authError.message);
      return;
    }

    const event = {
      summary: 'New Event',
      start: {
        dateTime: new Date().toISOString(), // Use the desired start time
      },
      end: {
        dateTime: new Date().toISOString(), // Use the desired end time
      },
    };

    const response = await fetch('https://www.googleapis.com/calendar/v3/calendars/primary/events', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${user.auth.token.access_token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(event),
    });

    if (response.ok) {
      console.log('Event created successfully');
    } else {
      console.log('Error creating event:', response.statusText);
    }
  }

  return (
    <div>
      <h1>Todo List</h1>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        placeholder="Enter a new todo"
      />
      <button onClick={addTodo}>Add Todo</button>
      <button onClick={createCalendarEvent}>Create Calendar Event</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {todo.task}
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
